/**
 * Validate the login credentials for a global administrator
 *
 * @param  {String}             username                    The username of the global administrator to attempt login validation for
 * @param  {String}             password                    The password to attempt login validation with
 * @param  {Function}           callback                    Standard callback function
 * @param  {Object}             callback.err                An error object, if any
 * @param  {GlobalAdmin}        callback.globalAdmin        The global administrator for the provided username and password. If the provided password was incorrect, this will be `null`
 */
var validateLoginCredentials = module.exports.validateLoginCredentials = function(username, password, callback) {
    // TODO: username validation
    // TODO: password validation

    // Ensure that a global administrator with the provided username exist
    getGlobalAdminByUsername(username, function(err, globalAdmin) {
        // No global administrator could be found for the provided username
        if (err && err.code === 404) {
            log().info({'err': err, 'username': username}, 'The provided global administrator username is incorrect');
            return callback({'code': 401, 'msg': 'The provided global administratorusername is incorrect'});
        // A different error occured
        } else if (err) {
            log().error({'err': err, 'username': username}, 'Failed to validate the login credentials for a global administrator');
            return callback(err);
        }

        AuthUtil.comparePassword(password, globalAdmin.password, function(err, match) {
            if (err) {
                log().error({'err': err}, 'Failed to validate the password for a global administrator');
                return callback(err);
            } else if (!match) {
                log().info({'err': err, 'username': username}, 'The provided global administrator password is incorrect');
                return callback({'code': 401, 'msg': 'The provided global administrator password is incorrect'});
            }

            return callback(null, globalAdmin);
        });
    });
};
