/**
 * Get a global administrator by username
 *
 * @param  {String}             username                    The username of the global administrator to retrieve
 * @param  {Function}           callback                    Standard callback function
 * @param  {Object}             callback.err                An error object, if any
 * @param  {GlobalAdminUser}    callback.globalAdminUser    The requested global administrator
 */
var getGlobalAdminUserByUsername = module.exports.getGlobalAdminUserByUsername = function(username, callback) {
    DB.GlobalAdminUser.find({'where': {'username': username}}).complete(function(err, globalAdminUser) {
        if (err) {
            log().error({'err': err, 'username': username}, 'Failed to get a global administrator by username');
            return callback({'code': 500, 'msg': err.message});
        } else if (!globalAdminUser) {
            log().error({'err': err, 'username': username}, 'A global administrator with the specified username could not be found');
            return callback({'code': 404, 'msg': 'A global administrator with the specified username could not be found'});
        }

        return callback(null, globalAdminUser);
    });
};

/**
 * Create a new global administrator
 *
 * @param  {String}             username                    The username for the global administrator
 * @param  {String}             password                    The password for the global administrator
 * @param  {String}             displayName                 The name of the global administrator
 * @param  {Function}           callback                    Standard callback function
 * @param  {Object}             callback.err                An error object, if any
 * @param  {GlobalAdminUser}    callback.globalAdminUser    The created global administrator
 */
var createGlobalAdminUser = module.exports.createGlobalAdminUser = function(username, password, displayName, callback) {
    // Hash the provided password
    var hashedPassword = AuthUtil.hashPassword(password);

    var globalAdminUser = {
        'username': username,
        'password': hashedPassword,
        'displayName': displayName
    };

    DB.GlobalAdminUser.create(globalAdminUser).complete(function(err, globalAdminUser) {
        if (err) {
            log().error({'err': err}, 'Failed to create a new global administrator');
            return callback({'code': 500, 'msg': err.message});
        }

        log().debug({'globalAdminUser': globalAdminUser}, 'Created a new global administrator');
        return callback(null, globalAdminUser);
    });
};

