var DB = require('gh-core/lib/db');
var log = require('gh-core/lib/logger');

var getAppByHostname = module.exports.getAppByHostname = function(hostname, callback) {
    if (hostname === 'admin.grasshopper.com') {
        var adminApp = DB.App.build({
            'displayName': 'The global admin',
            'hostname': 'admin.grasshopper.com',
            'type': 'admin',
            'enabled': true
        });
        return callback(null, adminApp);
    }

    // Get the hostname from the database
    DB.App.find({'where': {'hostname': hostname}}).done(function(err, app) {
        if (err) {
            log().error({'err': err, 'hostname': hostname}, 'Unable to find an app by hostname');
            return callback({'code': 500, 'msg': 'Unable to find an app by hostname'});
        } else if (!app) {
            return callback({'code': 404, 'msg': 'No app found with that hostname'});
        }

        return callback(null, app);
    });
};
