var GrassHopper = require('gh-core');

/**
 * @REST getConfigSchema
 *
 * Get the configuration schema
 *
 * @Server      admin,app
 * @Method      GET
 * @Path        /config/schema
 * @Return      {ConfigSchema}                      The configuration schema
 */
var getConfigSchema = function(req, res) {
    res.sendStatus(501);
};

GrassHopper.globalAdminRouter.on('get', '/api/config/schema', getConfigSchema);
GrassHopper.appRouter.on('get', '/api/config/schema', getConfigSchema);

/**
 * @REST getConfig
 *
 * Get the configuration for the current app
 *
 * @Server      app
 * @Method      GET
 * @Path        /config
 * @Return      {Config}                            The configuration for the current app
 */
GrassHopper.appRouter.on('get', '/api/config', function(req, res) {
    res.sendStatus(501);
});

/**
 * @REST getConfigByApp
 *
 * Get the configuration for an app
 *
 * @Server      admin
 * @Method      GET
 * @Path        /config
 * @QueryParam  {number}            app             The id of the app to get the configuration for
 * @Return      {Config}                            The configuration for the specified app
 */
GrassHopper.globalAdminRouter.on('get', '/api/config', function(req, res) {
    res.sendStatus(501);
});

/**
 * @REST updateConfig
 *
 * Update a configuration value for the current app
 *
 * @Server      app
 * @Method      POST
 * @Path        /config
 * @BodyParam   {ConfigValues}      body            Configuration value(s) to update
 * @Return      {void}
 */
GrassHopper.appRouter.on('post', '/api/config', function(req, res) {
    res.sendStatus(501);
});

/**
 * @REST updateConfigByApp
 *
 * Update a configuration value for an app
 *
 * @Server      admin
 * @Method      POST
 * @Path        /config
 * @QueryParam  {number}            app             The id of the app to update the configuration value(s) for
 * @BodyParam   {ConfigValues}      body            Configuration value(s) to update
 * @Return      {void}
 */
GrassHopper.globalAdminRouter.on('post', '/api/config', function(req, res) {
    res.sendStatus(501);
});

/**
 * @REST clearConfig
 *
 * Clear a configuration value for the current app
 *
 * @Server      app
 * @Method      POST
 * @Path        /config/clear
 * @BodyParam   {string[]}          configFields    Name(s) of the configuration element(s) to clear
 * @Return      {void}
 */
GrassHopper.appRouter.on('post', '/api/config/clear', function(req, res) {
    res.sendStatus(501);
});

/**
 * @REST clearConfigByApp
 *
 * Clear a configuration value for an app
 *
 * @Server      admin
 * @Method      POST
 * @Path        /config/clear
 * @QueryParam  {number}            app             The id of the app to clear the configuration value for
 * @BodyParam   {string[]}          configFields    Name(s) of the configuration element(s) to clear
 * @Return      {void}
 */
GrassHopper.globalAdminRouter.on('post', '/api/config/clear', function(req, res) {
    res.sendStatus(501);
});
