/**
 * A generic context object that represents a user execution context
 *
 * @param  {App}        app                         The app of the current execution context
 * @param  {User}       user                        The user associated to the current execution context
 * @param  {User}       imposter                    The user that's impostering the current user
 */
var Context = module.exports.Context = function(app, user, imposter) {
    var that = {};

    that.app = app;
    that.user = user;
    that.imposter = imposter;

    return that;
};
