var GrassHopper = require('gh-core');

var DocsAPI = require('gh-docs');

/**
 * @REST getSwagger
 *
 * Get the swagger resources json
 *
 * @Api private
 * @Server      admin,app
 * @Method      GET
 * @Path        /swagger
 * @Return      {object}                  Swagger resource listing, @see https://github.com/wordnik/swagger-spec/blob/master/versions/1.2.md#51-resource-listing
 * @HttpResponse             200          Swagger resource listing available
 */
GrassHopper.appServer.on('get', '/api/swagger', function(req, res) {
    return res.status(200).send(DocsAPI.getResources(req.ctx));
});
GrassHopper.globalAdminServer.on('get', '/api/swagger', function(req, res) {
    return res.status(200).send(DocsAPI.getResources(req.ctx));
});

/**
 * @REST getSwaggerId
 *
 * Get the swagger apis json
 *
 * @Api private
 * @Server      admin,app
 * @Method      GET
 * @Path        /swagger/{id}
 * @PathParam   {string}     id           Resource id requested
 * @Return      {object}                  Swagger API declaration, @see https://github.com/wordnik/swagger-spec/blob/master/versions/1.2.md#52-api-declaration
 * @HttpResponse             200          Swagger api declaration available
 */
GrassHopper.appServer.on('get', '/api/swagger/:id', function(req, res) {
    return res.status(200).send(DocsAPI.getApi(req.ctx, req.params.id));
});
GrassHopper.globalAdminServer.on('get', '/api/swagger/:id', function(req, res) {
    return res.status(200).send(DocsAPI.getApi(req.ctx, req.params.id));
});
