var _ = require('lodash');

var GroupsDAO = require('./internal/dao');

/**
 * Determine whether or not the user in context can get a group's members
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {Group}      group                       The group whose members will be retrieved
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canGetMembers      `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canGetGroupMembers = module.exports.canGetGroupMembers = function(ctx, group, callback) {
    // Anonymous users can never retrieve a group's members
    if (!ctx.user) {
        return callback(null, false);

    // Administrators who can administer the group's application
    // can retrieve the group's members
    } else if (ctx.user.canAdmin(group.AppId)) {
        return callback(null, true);
    }

    // At this point we need to check if the user is a member of the group
    GroupsDAO.isMember(group, ctx.user, callback);
};

/**
 * Determine whether or not the user in context can get a user's memberships
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {User}       user                        The user whose memberships will be retrieved
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canGetMemberships  `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canGetUserMemberships = module.exports.canGetUserMemberships = function(ctx, user, callback) {
    // Anonymous users can never retrieve a user's membership
    if (!ctx.user) {
        return callback(null, false);

    // A user can always retrieve their own group memberships
    } else if (ctx.user.id === user.id) {
        return callback(null, true);

    // Administrators who can administer the user's application
    // can retrieve the user's memberships
    } else if (ctx.user.canAdmin(user.AppId)) {
        return callback(null, true);
    }

    return callback(null, false);
};

/**
 * Determine whether or not the user in context can update a group's members
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {Group}      group                       The group whose members will be updated
 * @param  {Object}     update                      The members to add to or remove from the group. The keys should hold the user ids and the value should be a boolean indicating whether the user should be added(true) or removed(false)
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canUpdateMembers   `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canUpdateGroupMembers = module.exports.canUpdateGroupMembers = function(ctx, group, update, callback) {
    // Ensure the current user can update the group. If you can
    // retrieve the members of a group, you are also allowed to update them
    canGetGroupMembers(ctx, group, callback);
};

/**
 * Determine whether or not the user in context can lock a group
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {Group}      group                       The group to lock
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canLockGroup       `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canLockGroup = module.exports.canLockGroup = function(ctx, group, callback) {
    // Ensure the current user can lock the group. If you can
    // retrieve the members of a group, you are also allowed to lock it
    canGetGroupMembers(ctx, group, function(err, canGetMembers) {
        if (err || !canGetMembers) {
            return callback(err, canGetMembers);
        }

        var canLock = false;
        if (!group.isLocked()) {
            canLock = true;
        } else if (group.LockedBy && group.LockedBy.id === ctx.user.id) {
            canLock = true;
        } else if (ctx.user.canAdmin(group.AppId)) {
            canLock = true;
        }
        return callback(null, canLock);
    });
};

/**
 * Determine whether or not the user in context can release a lock from a group
 *
 * @param  {Context}    ctx                         Standard context object containing the current user and the current application
 * @param  {Group}      group                       The group to release a lock from
 * @param  {Function}   callback                    Standard callback function
 * @param  {Object}     callback.err                An error that occurred, if any
 * @param  {Boolean}    callback.canReleaseLock     `true` if the user in context has the appropriate permission. `false` otherwise
 */
var canReleaseLock = module.exports.canReleaseLock = function(ctx, group, callback) {
    // Releasing a lock on a group requires the same permission as locking the group
    canLockGroup(ctx, group, callback);
};
