var _ = require('lodash');
var assert = require('assert');
var moment = require('moment');

var EventsTestsUtil = require('gh-events/tests/util');
var SeriesTestUtil = require('gh-series/tests/util');
var TestsUtil = require('gh-tests');

var OrgUnitTestUtil = require('./util');


describe('Organisational units - calendars', function() {

    describe('JSON', function() {

        /**
         * Test that verifies getting an organisational unit's calendar
         */
        it('verify getting a calendar', function(callback) {
            OrgUnitTestUtil.createSimpleTree(global.tests.admins.cam2013.client, function(course, subject, part, module, series) {

                // Get the events under the module
                var events = _.union(series[0].events, series[1].events, series[2].events, series[3].events);
                events = _.sortBy(events, 'start');

                // Verify the feed contains the correct events (and in the correct order)
                var start = moment(_.first(events).start).subtract(1, 'day').format();
                var end = moment(_.last(events).end).add(1, 'day').format();
                OrgUnitTestUtil.assertGetOrgUnitCalendar(global.tests.admins.cam2013.client, module.id, start, end, events, function(calendar) {
                    return callback();
                });
            });
        });

        /**
         * Test that verifies validation when getting an organisational unit's calendar
         */
        it('verify validation when getting a calendar', function(callback) {
            OrgUnitTestUtil.assertCreateOrgUnit(global.tests.admins.cam2013.client, 'Test org unit', 'course', null, function(orgUnit) {

                var start = moment().format();
                var end = moment().add(2, 'hour').format();

                // Invalid organisational unit id
                OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, 'Not a number', start, end, 400, function() {

                    // Missing organisational unit id
                    OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, -1, start, end, 404, function() {
                        OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, 234234234, start, end, 404, function() {

                            // Mising start date
                            OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, orgUnit.id, null, end, 400, function() {

                                // Missing end date
                                OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, orgUnit.id, start, null, 400, function() {

                                    // Invalid start date
                                    OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, orgUnit.id, 'bleh', end, 400, function() {

                                        // Invalid end date
                                        OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, orgUnit.id, start, 'bleh', 400, function() {

                                            // The start date cannot come after the end date
                                            OrgUnitTestUtil.assertGetOrgUnitCalendarFails(global.tests.admins.cam2013.client, orgUnit.id, end, start, 400, function() {
                                                return callback();
                                            });
                                        });
                                    });
                                });
                            });
                        });
                    });
                });
            });
        });
    });

    describe('iCal', function() {

        /**
         * Test that verifies getting an organisational unit's calendar in iCal format
         */
        it('verify getting a calendar', function(callback) {
            OrgUnitTestUtil.createSimpleTree(global.tests.admins.cam2013.client, function(course, subject, part, module, series) {

                // Verify the feed contains the correct events (and in the correct order)
                var events = _.union(series[0].events, series[1].events, series[2].events, series[3].events);
                events = _.sortBy(events, 'start');
                OrgUnitTestUtil.assertGetOrgUnitCalendarIcal(global.tests.admins.cam2013.client, module.id, events, function(calendar) {
                    return callback();
                });
            });
        });

        /**
         * Test that verifies validation when getting an organisational unit's calendar in iCal format
         */
        it('verify validation when getting a calendar', function(callback) {
            // Invalid organisational unit id
            OrgUnitTestUtil.assertGetOrgUnitCalendarIcalFails(global.tests.admins.cam2013.client, 'Not a number', 400, function() {

                // Missing organisational unit
                OrgUnitTestUtil.assertGetOrgUnitCalendarIcalFails(global.tests.admins.cam2013.client, -1, 404, function() {
                    OrgUnitTestUtil.assertGetOrgUnitCalendarIcalFails(global.tests.admins.cam2013.client, 345345345, 404, function() {
                        return callback();
                    });
                });
            });
        });
    });

    describe('RSS', function() {

        /**
         * Test that verifies getting an organisational unit's calendar in RSS format
         */
        it('verify getting a calendar', function(callback) {
            OrgUnitTestUtil.createSimpleTree(global.tests.admins.cam2013.client, function(course, subject, part, module, series) {

                // Verify the feed contains the correct events (and in the correct order)
                var events = _.union(series[0].events, series[1].events, series[2].events, series[3].events);
                events = _.sortBy(events, 'start');
                OrgUnitTestUtil.assertGetOrgUnitCalendarRss(global.tests.admins.cam2013.client, module.id, events, function(calendar) {
                    return callback();
                });
            });
        });

        /**
         * Test that verifies validation when getting an organisational unit's calendar in RSS format
         */
        it('verify validation when getting a calendar', function(callback) {
            // Invalid organisational unit id
            OrgUnitTestUtil.assertGetOrgUnitCalendarRssFails(global.tests.admins.cam2013.client, 'Not a number', 400, function() {

                // Missing organisational unit
                OrgUnitTestUtil.assertGetOrgUnitCalendarRssFails(global.tests.admins.cam2013.client, -1, 404, function() {
                    OrgUnitTestUtil.assertGetOrgUnitCalendarRssFails(global.tests.admins.cam2013.client, 345345345, 404, function() {
                        return callback();
                    });
                });
            });
        });
    });
});
