module.exports = function(client) {
    client.tenant = {};

    /**
     * Get all tenants
     *
     * @param  {Function}       callback                Standard callback function
     * @param  {Object}         callback.err            An error that occurred, if any
     * @param  {Object}         callback.body           The JSON response from the REST API
     * @param  {Response}       callback.response       The response object as returned by requestjs
     * @see gh-tenants/lib/rest.js for more information
     */
    client.tenant.getTenants = function(callback) {
        client._request('/api/tenants', 'GET', null, null, callback);
    };

    /**
     * Get a tenant
     *
     * @param  {Number}         id                      The id of the tenant to retrieve
     * @param  {Function}       callback                Standard callback function
     * @param  {Object}         callback.err            An error that occurred, if any
     * @param  {Object}         callback.body           The JSON response from the REST API
     * @param  {Response}       callback.response       The response object as returned by requestjs
     * @see gh-tenants/lib/rest.js for more information
     */
    client.tenant.getTenant = function(id, callback) {
        client._request('/api/tenants/' + id, 'GET', null, null, callback);
    };

    /**
     * Create a new tenant
     *
     * @param  {String}         displayName             The name of the tenant
     * @param  {Function}       callback                Standard callback function
     * @param  {Object}         callback.err            An error that occurred, if any
     * @param  {Object}         callback.body           The JSON response from the REST API
     * @param  {Response}       callback.response       The response object as returned by requestjs
     * @see gh-tenants/lib/rest.js for more information
     */
    client.tenant.createTenant = function(displayName, callback) {
        client._request('/api/tenants', 'POST', {'displayName': displayName}, null, callback);
    };

    /**
     * Update a tenant
     *
     * @param  {Number}         id                      The id of the tenant to update
     * @param  {String}         displayName             The name of the tenant
     * @param  {Function}       callback                Standard callback function
     * @param  {Object}         callback.err            An error that occurred, if any
     * @param  {Object}         callback.body           The JSON response from the REST API
     * @param  {Response}       callback.response       The response object as returned by requestjs
     * @see gh-tenants/lib/rest.js for more information
     */
    client.tenant.updateTenant = function(id, displayName, callback) {
        client._request('/api/tenants/' + id, 'POST', {'displayName': displayName}, null, callback);
    };

};
