var GrassHopper = require('gh-core');

/**
 * @REST getSeries
 *
 * Get an event series
 *
 * @Server      admin,app
 * @Method      GET
 * @Path        /series/{id}
 * @PathParam   {number}            id                  The id of the event series to retrieve
 * @Return      {Series}                                The requested event series
 */
var getSeries = function(req, res) {
    res.send(501);
};

GrassHopper.globalAdminServer.on('get', '/api/series/:id', getSeries);
GrassHopper.appServer.on('get', '/api/series/:id', getSeries);

