var lynx = require('lynx');

// When we don't need to collect data through statsd we will return
// a stubbed lynx object
var metrics = {
    'increment': function() {},
    'decrement': function() {},
    'timing': function() {},
    'gauge': function() {},
    'set': function() {},
    'createTimer': function() {
        return {
            'stop': function() {}
        };
    }
};

/**
 * Initialise the Statsd module
 *
 * @param  {Object}     config      The statsd object from the main `config.js` file
 */
var init = module.exports.init = function(config) {
    if (config.enabled) {
        metrics = new lynx(config.host, config.port, {'scope': config.scope});
    }
};

/**
 * Get a statsd object. If statsd is disabled, a stubbed object will be returned
 *
 * @return {Lynx} An object that allows you to push data to statsd
 * @see https://github.com/dscape/lynx
 */
var getInstance = module.exports.getInstance = function() {
    return metrics;
};
