var log = require('gh-core/lib/logger').logger('gh-tenants');
var Validator = require('gh-core/lib/validator').Validator;

var TenantsDAO = require('./internal/dao');

/**
 * Get all tenants
 *
 * @param  {Context}        ctx                 Standard context containing the current user and the current app
 * @param  {Function}       callback            Standard callback function
 * @param  {Object}         callback.err        An error object, if any
 * @param  {Tenants[]}      callback.tenants    All available tenants
 */
var getTenants = module.exports.getTenants = function(ctx, callback) {
    callback = callback || function() {};

    // Validation
    var validator = new Validator();
    validator.check(null, {'code': 401, 'msg': 'Only global administrators can get a tenant'}).isGlobalAdmin(ctx);
    if (validator.hasErrors()) {
        return callback(validator.getFirstError());
    }

    TenantsDAO.getTenants(callback);
};

/**
 * Get a tenant
 *
 * @param  {Context}        ctx                 Standard context containing the current user and the current app
 * @param  {Number}         id                  The id of the tenant to retrieve
 * @param  {Function}       callback            Standard callback function
 * @param  {Object}         callback.err        An error object, if any
 * @param  {Tenant}         callback.tenant     The requested tenant
 */
var getTenant = module.exports.getTenant = function(ctx, id, callback) {
    callback = callback || function() {};

    // Validation
    var validator = new Validator();
    validator.check(null, {'code': 401, 'msg': 'Only global administrators can get a tenant'}).isGlobalAdmin(ctx);
    validator.check(id, {'code': 400, 'msg': 'A valid tenant id must be provided'}).isInt();
    if (validator.hasErrors()) {
        return callback(validator.getFirstError());
    }

    TenantsDAO.getTenant(id, callback);
};

/**
 * Create a new tenant
 *
 * @param  {Context}        ctx                 Standard context containing the current user and the current app
 * @param  {String}         displayName         The name of the tenant
 * @param  {Function}       callback            Standard callback function
 * @param  {Object}         callback.err        An error object, if any
 * @param  {Tenant}         callback.tenant     The created tenant
 */
var createTenant = module.exports.createTenant = function(ctx, displayName, callback) {
    callback = callback || function() {};

    // Validation
    var validator = new Validator();
    validator.check(null, {'code': 401, 'msg': 'Only global administrators can create a new tenant'}).isGlobalAdmin(ctx);
    validator.check(displayName, {'code': 400, 'msg': 'A tenant name must be provided'}).notEmpty();
    validator.check(displayName, {'code': 400, 'msg': 'A tenant name can be at most 256 characters long'}).isShortString();
    if (validator.hasErrors()) {
        return callback(validator.getFirstError());
    }

    TenantsDAO.createTenant(displayName, callback);
};

/**
 * Update a tenant
 *
 * @param  {Context}        ctx                 Standard context containing the current user and the current app
 * @param  {Number}         id                  The id of the tenant to update
 * @param  {String}         displayName         Updated tenant name
 * @param  {Function}       callback            Standard callback function
 * @param  {Object}         callback.err        An error object, if any
 * @param  {Tenant}         callback.tenant     The updated tenant
 */
var updateTenant = module.exports.updateTenant = function(ctx, id, displayName, callback) {
    callback = callback || function() {};

    // Validation
    var validator = new Validator();
    validator.check(null, {'code': 401, 'msg': 'Only global administrators can update a tenant'}).isGlobalAdmin(ctx);
    validator.check(id, {'code': 400, 'msg': 'A valid tenant id must be provided'}).isInt();
    validator.check(displayName, {'code': 400, 'msg': 'An updated tenant name must be provided'}).notEmpty();
    validator.check(displayName, {'code': 400, 'msg': 'An updated tenant name can be at most 256 characters long'}).isShortString();
    if (validator.hasErrors()) {
        return callback(validator.getFirstError());
    }

    // Verify that the provided tenant exists
    getTenant(ctx, id, function(err, tenant) {
        if (err) {
            return callback(err);
        }

        TenantsDAO.updateTenant(tenant, displayName, callback);
    });
};
