var GrassHopper = require('gh-core');

/**
 * @REST getTenants
 *
 * Get all tenants
 *
 * @Server      admin
 * @Method      GET
 * @Path        /tenants
 * @Return      {Tenant[]}                          All available tenants
 */
GrassHopper.globalAdminServer.get('/api/tenants', function(req, res) {
    res.send(501);
});

/**
 * @REST getTenant
 *
 * Get a tenant
 *
 * @Server      admin
 * @Method      GET
 * @Path        /tenants/{id}
 * @PathParam   {number}           id               The id of the tenant to retrieve
 * @Return      {Tenant}                            The requested tenant
 */
GrassHopper.globalAdminServer.get('/api/tenants/:id', function(req, res) {
    res.send(501);
});

/**
 * @REST createTenant
 *
 * Create a new tenant
 *
 * @Server      admin
 * @Method      POST
 * @Path        /api/tenants
 * @FormParam   {string}            displayName     The name of the tenant
 * @Return      {Tenant}                            The created tenant
 */
GrassHopper.globalAdminServer.post('/api/tenants', function(req, res) {
    res.send(501);
});

/**
 * @REST updateTenant
 *
 * Update a tenant
 *
 * @Server      admin
 * @Method      POST
 * @Path        /tenants/{id}
 * @PathParam   {number}            id              The id of the tenant to update
 * @FormParam   {string}            [displayName]   Updated tenant name
 * @Return      {Tenant}                            The updated tenant
 */
GrassHopper.globalAdminServer.post('/api/tenants/:id', function(req, res) {
    res.send(501);
});

