var assertGetTenant = module.exports.assertGetTenant = function(client, id, expectedTenant, callback) {
    client.tenant.getTenant(id, function(err, tenant) {
        assert.ok(!err);
        assert.ok(tenant);
        assert.ok(tenant.id);
        assert.ok(tenant.displayName);
        assert.ok(tenant.createdAt);
        assert.ok(tenant.updatedAt);

        // TODO
        if (expectedTenant) {
            assert.equal(tenant.id, expectedTenant.id);
            assert.equal(tenant.displayName, expectedTenant.displayName);
            assert.equal(tenant.createdAt, expectedTenant.createdAt);
            assert.equal(tenant.updatedAt, expectedTenant.updatedAt);
        }

        return callback(tenant);
    });
};

