/**
 * @REST getUsers
 *
 * Get all users for a tenant or app
 *
 * @Server      admin
 * @Method      GET
 * @Path        /users
 * @QueryParam  {number}            [appId]                 The id of the app to retrieve the users for
 * @QueryParam  {number}            [tenantId]              The id of the tenant to retrieve the users for
 * @QueryParam  {number}            [items]                 The maximum number of results to retrieve. Default: 10
 * @QueryParam  {number}            [page]                  The paging number of the results to retrieve
 * @Return      {UserList}                                  The users for the requested tenant or app
 */
GrassHopper.globalAdminServer.on('get', '/api/users', function(req, res) {
    res.send(501);
});

