/**
 * @REST getTenantUsers
 *
 * Get the users for a tenant
 *
 * @Server      admin,tenant
 * @Method      GET
 * @Path        /tenants/{tenantId}/users
 * @PathParam   {number}            tenantId        The id of the tenant for which to get the users
 * @QueryParam  {number}            [items]         The maximum number of results to retreive. Default: 10
 * @QueryParam  {number}            [page]          The paging number of the results to retrieve
 * @Return      {Users}                             The users for the tenant
 */
var getAppAdmins = function(req, res) {
    res.send(501);
};

GrassHopper.globalAdminRouter.on('get', '/api/tenants/:tenantId/users', getAppAdmins);
GrassHopper.tenantRouter.on('get', '/api/tenants/:tenantId/users', getAppAdmins);

/**
 * @REST getUser
 *
 * Get a user
 *
 * @Server      admin
 * @Method      GET
 * @Path        /users/{id}
 * @PathParam   {number}            id              The id of the user to retrieve
 * @Return      {User}                              The requested user
 */
GrassHopper.globalAdminRouter.on('get', '/api/users/:id', function(req, res) {
    res.send(501);
});

/**
 * @REST getMe
 *
 * Get the current user
 *
 * @Server      admin,tenant
 * @Method      GET
 * @Path        /me
 * @Return      {Me}                                The current user
 */
var getMe = function(req, res) {
    res.send(501);
};

OAE.globalAdminRouter.on('get', '/api/me', getMe);
OAE.tenantRouter.on('get', '/api/me', getMe);

/**
 * @REST createUser
 *
 * Create a new app
 *
 * @Server      admin
 * @Method      POST
 * @Path        /api/tenants/{tenantId}/apps
 * @PathParam   {number}            tenantId        The id of the tenant on which the app should be created
 * @FormParam   {string}            displayName     The name of the app
 * @FormParam   {string}            host            The host on which the app can be found
 * @FormParam   {string}            type            The type of the app (e.g. `timetable`)
 * @Return      {App}                               The created app
 */
GrassHopper.globalAdminRouter.on('post', '/api/tenants/:tenantId/apps', function(req, res) {
    res.send(501);
});


