var assert = require('assert');

var TestsUtil = require('gh-tests/lib/util');

describe('Users', function() {

    describe('create', function() {

        /**
         * Test that verifies that a user can be created
         */
        it('verify a user can be created', function(callback) {
            TestsUtil.generateTestUsers(global.tests.apps.cam2014, 1, false, function(simong) {
                assert.ok(simong);

                simong.client.user.getMe(function(err, me) {
                    assert.ok(!err);
                    assert.strictEqual(me.displayName, simong.profile.displayName);
                    return callback();
                });
            });
        });
    });
});
